    --color-blue-600: #rgbify(@blue) [];
    --color-blue-700: #rgbify(@blue) [];
    --color-blue-800: #rgbify(@blue) [];
    --color-blue-900: #rgbify(@blue) [];
    --color-blue-1000: #rgbify(@blue) [];
    --color-green-100: #rgbify(@teal) [];
