    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accentColor];
    @contrast-color: @catppuccin[@@lookup][@@contrastColor];

    color-scheme: if(@lookup = latte, light, dark);

    ::selection {
      background-color: fade(@accent-color, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    .login[data-v-0d5bfbcd],
    .login[data-v-0d5bfbcd]:hover {
      color: @base;
    }

    [fill="#3db4f2" i] {
      fill: @accent !important;
    }

    & when not (@lookup =latte) {
      .nav {
        background: @mantle;
      }

      .external-link {
        background: @surface1;

        [stroke="#fff" i] {
          stroke: @text !important;
        }
      }

      .external-link .icon,
      .dropdown-menu:hover {
        --color-white: #rgbify(@crust) [];
      }

      [fill="#fff" i] {
        fill: @crust !important;
      }
    }

    & when (@lookup =latte) {
      --color-overlay: #rgbify(@text) [];

      .external-link {
        background: @subtext0;
      }

      .nav {
        background: @text;
      }

      a.title {
        --color-white: #rgbify(@base) [];
      }
    }
  }
}

#rgbify(@color) {
  @rgb-raw: red(@color), green(@color), blue(@color);
}

