    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }
    * {
      --text-color: @text;
      --color-base: @text;
      --color-bg: @crust;
      --color-secondary: @subtext0;
      --color-ad-raised: @surface2;
