/* ==UserStyle==
@name         Catppuccin-MAL
@version      1.0.0
@description  Catppuccin theme for MyAnimeList.net
@namespace    orangc
@author       orangc
@homepageURL  https://github.com/orxngc/random-themes
@updateURL    https://raw.githubusercontent.com/orxngc/random-themes/main/myanimelist.css
==/UserStyle== */
@-moz-document domain("myanimelist.net") {
  :root {
    --rosewater: #f5e0dc;
    --flamingo: #f2cdcd;
    --pink: #f5c2e7;
    --mauve: #cba6f7;
    --red: #f38ba8;
    --maroon: #eba0ac;
    --peach: #fab387;
    --yellow: #f9e2af;
    --green: #a6e3a1;
    --teal: #94e2d5;
    --sky: #89dceb;
    --sapphire: #74c7ec;
    --blue: #89b4fa;
    --lavender: #b4befe;
